<?php declare(strict_types=1); session_start(); define('BASE_PATH', __DIR__); define('DB_HOST', 'localhost'); define('DB_USER', 'root'); define('DB_PASS', '123admin@_'); define('DB_NAME', 'karnak'); function dbConnect(): \PDO { return new \PDO( 'mysql:host='.DB_HOST.';dbname='.DB_NAME.';charset=utf8mb4', DB_USER, DB_PASS, [ \PDO::ATTR_ERRMODE => \PDO::ERRMODE_EXCEPTION, \PDO::ATTR_DEFAULT_FETCH_MODE => \PDO::FETCH_ASSOC, ] ); } function redirectIfLoggedIn(string $redirectTo = 'dashboard.php'): void { if (isset($_SESSION['user_id'])) { header('Location: '.$redirectTo); exit; } } ?>